    def checkCorrectness(self,name, surname, phoneNumber, email, password, passwordConfirm, pesel):
        if((name=="")|(surname=="")|(phoneNumber=="")|(email=="")|(password=="")|(passwordConfirm=="")|(pesel=="")):
            wx.MessageBox("Wszystkie pola musza byc uzupelnione!")
            return False
        elif not (re.search("^[a-zA-Z]*$",name)):
            wx.MessageBox("Imie musi skladac sie jedynie z wielkich i malych liter!")
            return False
        elif not (re.search("^[a-zA-Z]*$",surname)):           
            wx.MessageBox("Nazwisko musi skladac sie jedynie z wielkich i malych liter!")
            return False
        elif not (validate_email(email)):
            wx.MessageBox("Niepoprawny adres email!")
            return False
        elif not (re.search("^[0-9]{11}$",pesel)):
            wx.MessageBox("Niepoprawny numer PESEL!")
            return False
        elif not (re.search("^[0-9]{9}$",phoneNumber)):
            wx.MessageBox("Niepoprawny numer telefonu!")
            return False
        elif (len(password)<8):
            wx.MessageBox("Haslo zbyt krotkie!")
            return False
        elif(password != passwordConfirm):
            wx.MessageBox("Hasla nie sa takie same!")
            return False  
        else:
            return True
    def accept(self, event):  # wxGlade: Register.<event_handler>
        name = self.userNameInput.GetValue()
        surname = self.userSurnameInput.GetValue()
        phoneNumber = str(self.userPhoneNumberInput.GetValue())
        email = str(self.userEmailAdressInput.GetValue())
        passwordConfirm = self.userPasswordConfirmInput.GetValue()
        password = self.userPasswordInput.GetValue()
        pesel = str(self.userPESELInput.GetValue())
        if self.buttonFemale.GetValue():
            gender = "Kobieta"
        else:
            gender = "Mężczyzna"
        if  self.checkCorrectness(name, surname, phoneNumber, email, password, passwordConfirm, pesel):

            message = register(pesel, name, surname, gender,password, email, phoneNumber)
            if message:
                print('Zarejestrowano')
                Login(self).Show()
                self.Hide()
            else:
                print('Niezarejestrowano')
            event.Skip()

